// Pseudo-random function
float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898,78.233))) * 43758.5453123);
}

// 2D noise function
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners of cell
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth interpolation
    vec2 u = f * f * (3.0 - 2.0 * f);

    // Mix the four corners
    return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Scale the coordinate system
    vec2 uv = fragCoord/iResolution.xy;

    // Add multiple layers of noise with different frequencies
    float scale = 4.0;
    float height = 0.0;

    // Animate the noise by offsetting based on time
    vec2 offset = vec2(iTime * 0.3);

    // Layer 1: Base noise
    height += noise((uv + offset) * scale) * 0.5;

    // Layer 2: Higher frequency detail
    height += noise((uv - offset * 0.5) * scale * 2.0) * 0.25;

    // Layer 3: Fine detail
    height += noise((uv + offset * 0.2) * scale * 4.0) * 0.125;

    // Normalize to [0,1] range
    height = height * 0.8 + 0.1;

    // Output the height map
    fragColor = vec4(height, height, height, 1.0);
}
