vec3 pallete( float f ){
   vec3 a = vec3(0.5, 0.5, 0.5);
   vec3 b = vec3(0.75, 0.75, 0.75);
   vec3 c = vec3(1.0, 1.0, 1.0);
   vec3 d = vec3(0.225, 0.45, 0.675);

   return a + b * cos(6.283 * (c*f+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

    for (float i = 0.0; i < 3.0; i++)
    {
       uv = fract(uv * 1.5) - 0.5;
       float distance = length(uv) * exp(-length(uv0));
       vec3 color = pallete(length(uv0) + i * 0.5 + iTime* 0.5);
       distance = sin(distance * 8.0 + iTime)/8.0;
       distance = abs(distance);
       distance = 0.01/distance;
       finalColor += color * distance;
    }
    fragColor = vec4 (finalColor, 1);
}
