#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U)
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
vec2 a, da, b, db;
#define G -.0002
float pendulum (int n) {
    float pl = 0.;
    for (int i = 0; i < n; i++) {

        vec2 dda = vec2(0,G);
        vec2 ddb = vec2(0,G);
		vec2 dr = b-a;
        float l = length(dr);
        float f = (.2-l)/l;
        float la = length(a);
        dda -= dr*f-a*(.2-la)/la;
        ddb += dr*f;
        da += dda;
        db += ddb;
        a += da;
        b += db;

        pl += length(da-db);
    }
    return pl;
}

float ln (vec2 p, vec2 a, vec2 b) { 
    return length(p-a-(b-a)*clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.));
}
