vec4 mb(vec2 c) {
    vec2 z = vec2(0.);

    int m = 256;
    int i = 1;
    float l = .0;
    bool ignore = false;
    for (; i <= m; ++i) {
        z = vec2(z.x * z.x - z.y * z.y, 2.0 * z.x * z.y) + c;
        float tl = length(z);
        if (tl > 2.) {
            l = tl;
            i = 1;
            break;
        }
    }

    return vec4(z, float(i), l);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zt = iTime * .03;
    float zf = 5.5;
    float zoom = exp(sin(zt) * zf - zf + 1.09861);
    float tran = exp(sin(zt) * zf - zf);
    vec2 pos = mix(vec2(-.5,.6032135), vec2(0.), tran);
    vec2 c = (fragCoord/iResolution.y - iResolution.xy * .5 / iResolution.y) * zoom + pos;
    float aa = 0.0005 * zoom;

    vec4 r = mb(c + vec2(aa, 0.));
    r += mb(c + vec2(-aa, 0.));
    r += mb(c + vec2(0., aa));
    r += mb(c + vec2(0., -aa));
    r /= 4.;

    vec2 z = r.xy;
    float i = r.z;
    float l = r.w;

    // Output to screen
    float t = iTime * 1.5 + atan(z.y, z.x) + length(z) * 12.56637 * float(i) / 30.;
    float sc = float(i) * iTime * 10.;
    vec3 v = smoothstep(-.75, .75, vec3(sin(l / sc + t)));
    fragColor = vec4(v, 1.);
}
