#define time iTime

float circle(vec2 v, float s) {
    return s-length(v);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.xy -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    uv.xy /= 2.0;
    vec3 col = vec3(0.0);

    for (int x = 6; x < 12; x++) {
        float i = float(x);
        vec2 pos = uv.xy;
        float z = sin(time*i + i) / 10.0;
        float p = cos(time*i - i) / 10.0;
        pos.xy += vec2(z, p);
        float c = circle(pos.xy, .101) * 8.0;
        col.b += exp(tan(c)) / c / 1000.0;
        col.r += c + smoothstep(.97,1.0, tan(c));
    }
	fragColor = vec4(col,1.0);

}
