#define HASHSCALE1 .1031

float rnd12( vec2 p ) {

    vec3 
    p3  = fract( vec3( p.xyx ) * HASHSCALE1 - iTime );
    p3 += dot( p3, p3.yzx + 19.19 );
    return fract( ( p3.x + p3.y ) * p3.z );
}

void mainImage( out vec4 o, in vec2 i ) {

    // Normalized pixel coordinates (from 0 to 1)
    vec2 
        I = i / iResolution.xy;

    o = i.x < iMouse.x
        ? mix( texture( iChannel0, I ), texture( iChannel1, I ), iMouse.y / iResolution.y )
        : rnd12( i ) > iMouse.y / iResolution.y
            ? texture( iChannel0, I )
            : texture( iChannel1, I );
}
