float divisions = 5.;
float modulationDepth = 1.;

vec4 gradient(float f)
{
    vec4 c = vec4(0);
	f = mod( 0.5 + abs(sin(iTime / 15.)) * 0.5 * f, 1.5);
    for (int i = 0; i < 3; ++i)
        c[i] = pow(.5 + .5 * tan(2.0 * (f +  .2*float(i))), 10.0);
    return c;
}


float offset(float th)
{
    return modulationDepth * sin(divisions * th)*sin(iTime);
}


vec4 tunnel(float th, float radius)
{
	return gradient(offset(th + .65*iTime) + 3.*log(3.*radius) - iTime);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord - .5 * iResolution.xy) / iResolution.y;
	fragColor = tunnel(atan(p.y, p.x), length(p));
    //fragColor = gradient(p.x); // see gradient
}
