vec3 lerp(vec3 a, vec3 b, float r) { return mix(a,b,r); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Tweak your color cube here:
    vec3 r = vec3(1.0, 0.0, 0.0);
    vec3 g = vec3(0.0, 0.0, 0.0);
    vec3 b = vec3(0.0, 0.0, 1.0);
    vec3 rg = vec3(1.0, 1.0, 0.0);
    vec3 rb = vec3(1.0, 0.0, 1.0);
    vec3 gb = vec3(0.0, 1.0, 1.0);
    vec3 white = vec3(1.0, 1.0, 1.0);
    vec3 black = vec3(0.0, 0.0, 0.0);

	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 color = texture(iChannel0, uv);

    vec3 ta = lerp(black, r, color.r);
    vec3 tb = lerp(g, rg, color.r);
    vec3 tc = lerp(b, rb, color.r);
    vec3 td = lerp(gb, white, color.r);

    vec3 ab = lerp(ta, tb, color.g);
    vec3 cd = lerp(tc, td, color.g);

    vec3 abcd = lerp(ab, cd, color.b);

	fragColor = vec4(abcd, 1);
}
