#define MODE 1

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 col = vec3(0.);

    for(int i = 0; i < 50000; ++i)
    {
        #if MODE == 1
        col = vec3(mix(0.,1.,step(uv.x,0.5)));
        #elif MODE == 2
        col = vec3((uv.x>.5)?0.:1.);
        #elif MODE == 3
        if (uv.x > .5)
        	col = vec3(0.);
       	else
            col = vec3(1.);
        #endif
    }

	fragColor = vec4(col,1.0);
}
