//#define VISUALIZE_UV
#define VISUALIZE_CIRCLE_DF


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;
    uv *= 2.5+sin(iTime*.25)*2.;

    for (int i = 0; i < 22; i++) {
        vec2 dir = -normalize(abs(mod(uv,.5)-.25));
        uv = uv.yx + dir * .2 * sin(atan(uv.y,uv.x)+iTime*.14);
        uv = abs(uv);
    }

    #ifdef VISUALIZE_UV
	fragColor = vec4(abs(uv)*2.,0.,1.0);
	#endif

    #ifdef VISUALIZE_CIRCLE_DF
    float dist = (length(uv)-.5) + iTime*.03;
    fragColor = vec4(sin(dist*3.5+.46),
                     cos(dist*4.6),
                     -sin(dist*8.6), 1.)*.5+.5;
    #endif
}
