// graph : y=sin(2*pi*x+t)/2 + 0.5

vec4 SineCurve( vec2 uv,float t,float alpha){

    float x=2. *3.14 * uv.x; // 2*pi*x 
    float y=uv.y;

    float diff=sin(x+t)/2.+0.5-y;// get diff of equation
    float intensity = 1./ diff;// the bigger diff, the lower intensity

    float band=0.005;// line width adjustment

    return vec4(abs(intensity*band)*alpha, 0., 0., 1.);
}


void mainImage( out vec4 fragColor,  vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;  // uv : range 0..1

    float t=iTime * 1.; // time adjusted
    vec4 col = vec4(0.0);
    for(float i=1.0;i<5.0;i+=0.2){
    	col+=SineCurve(uv,t*i,i-2.);
    }

    fragColor = col;
}
