//	mod of https://www.shadertoy.com/view/MtjXzc

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5 -iTime*.1;
    vec3 c = cos(vec3(iTime*.06,
                  iTime*.045,
                  iTime*.015))*2.+2.;
    for (int i = 0; i < 27; i++) {
        vec3 p = vec3(uv*float(i),float(i));
//      c += abs( cos( ( c + sin( p )).yzx ) );
    	c += abs( vec3( cos(c.y+sin(p.x)),
                   cos(c.z+sin(p.z)),
                   -cos(c.x+sin(p.y)) ) );
    }
    fragColor = vec4((c*.04-.66)*3.,1.);
}
