void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color = vec4(0,0,0,0);

    vec4 blend01 = vec4(0,0,0,0);
    vec4 blend02 = vec4(0,0,0,0);
    vec4 blend03 = vec4(0,0,0,0);

    float freq01;
    float freq02;    


    vec2 move;
    move.x = sin(iTime)*0.7+0.5+cos(iTime+0.45)*0.2;
    move.y = cos(iTime*0.7)*0.7+0.5+sin(iTime+0.25)*0.2;

    uv.x -= move.x;
    uv.y -= move.y;
	vec4 tex01 = vec4(0,0,0,0);

    if(distance(uv,vec2(0.0,0.0))<0.4){

        uv /= 1.7*(1.0 - distance(uv, vec2(0.0, 0.0)));
		tex01 = texture(iChannel0,uv+vec2(move));


        blend01 = tex01;
        blend01.x = 0.0;

        blend02 = tex01;
        blend02.y = 0.0;

        blend03 = tex01;
        blend03.z = 0.0;

        freq01 = (sin(iTime)+1.0);
        freq02 = (cos(iTime)+1.0);

        tex01 = ( blend01*freq01 + blend02*freq02 + blend03*(3.0-freq01-freq02))/3.0;

    }
    else{
        tex01 = texture(iChannel0,uv+vec2(move));
    }


    fragColor = tex01;

}
