#define Circle(uv,i) step(length(uv),i)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = (2.*fragCoord - iResolution.xy ) / iResolution.y;
  fragColor=vec4(0);//Initialize

  for(float i=0.1;i<2.2;i+=0.1){
  vec3 col = vec3(i,1.0,1.0);
  fragColor += vec4(col*Circle(uv,i)+sin(iTime),1.0)*(i/20.0);
    }
}
