#define PI (3.14)

float loopTime = 10.;

float getTimeInLoopCircle()
{
    // t from 0 to Pi
    float t = fract(iTime/loopTime);

    // return sin(t);
    return abs(t-0.5);
}

float getTimeInLoop()
{
    // t from 0 to Pi
    return fract(iTime/loopTime);
}

float sound(float freq)
{
	return texture(iChannel0,vec2(freq,.25)).r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor.xyzw = vec4(0.);

    float t = getTimeInLoop();

	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 uvGraph = (uv - vec2(0.5,0.5));

    vec2 d = iMouse.xy / iResolution.xy;

    float epsi = t;
    /*if (abs(0.5*(sin(uvGraph.x*20.))  - uvGraph.y) < epsi)
    	fragColor.r = 1.;*/

    //fragColor.r = 1.0 - (abs(0.5*(son(uvGraph.x))  - uvGraph.y) + );
    fragColor.r = 1.0 - (abs(0.5*(sin(uvGraph.x*20. + (d.x + 0.5)*4.*2.*PI*getTimeInLoop()))  - uvGraph.y) + sound(0.1) );

}
