void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 O=vec2(iResolution.x/2.,iResolution.y/2.);
    vec2 p=vec2(fragCoord.x,fragCoord.y);
    vec2 pnorm=normalize(p-O);

    vec2 up=vec2(0,1.);
    vec2 down=vec2(0,-1.);
    vec2 left=vec2(1.,0.);
    vec2 right=vec2(-1.,0.);




    float c=fract((length(p-O)+50.*dot(up,pnorm))/length(vec2(0,0)-O)*10.-iTime*0.5);

    float d=fract((length(p-O)+100.*dot(up,pnorm))/length(vec2(0,0)-O)*10.-iTime);

    float g=fract((length(p-O)+150.*dot(up,pnorm))/length(vec2(0,0)-O)*10.-iTime*2.);


	fragColor = vec4(1./c,d/2.,g,1.0);

    //fragColor = vec4(c/2.,d/3.,g,1.0);




}
