void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float w = 0.01 * (0.5 + 0.5*sin(3.14*-0.5 + 1.4*iTime));

    float d1 = w*sin(2.1*iTime+17.0*uv.xy.y);
    float d2 = w*sin(2.4*iTime+21.0*uv.xy.y+0.7);

    float r = texture(iChannel0,vec2(uv.xy.x+d2,uv.xy.y)).r;
    float g = texture(iChannel0,vec2(uv.xy.x,uv.xy.y+d1)).g;
    float b = texture(iChannel0,vec2(uv.xy.x,uv.xy.y)).b;

    fragColor = vec4(r, g, b, 1.0);
}
