vec3 colorA = vec3(.99,.37,.33);
vec3 colorB = vec3(0.0,0.0, 0.76);


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 t;

    t.r = smoothstep(-0.2, 1.0 + (sin(iTime * 0.6)), uv.y);
    t.g = sin(uv.y * 3.14);
    t.b = pow(uv.y,0.5);

    vec3 color = mix(colorA,colorB,t); //mix(color1,color2, % mix for the 2 colros);
    fragColor = vec4(color,t);

}
