// from https://www.shadertoy.com/view/4lsSzX
// thanks to @FabriceNeyret2
// twitter.com/williammanco


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Variables
    float col = 20.0;
    float thickness = 1.05;
    float antialiasing = 0.001;
    float intensity = 1.0;
    float speed = 0.9;
    vec2 offset = vec2(1.0,1.0);
   	vec4 color = vec4(0.0,0.0,1.0,1.0);

    vec2 uv = (fragCoord.xy/iResolution.x)*col;

    //offset animation
	vec2 next = vec2(offset.x, iTime*speed);
    uv += next;

    //grid
    vec2 grep = abs(fract(uv) - 0.5);
    float grid = max(grep.x,grep.y)*thickness;

    //antialiasing
	float a = antialiasing;
	color *= smoothstep(0.48-a,0.49+a,grid);

	fragColor = vec4(color*intensity);
}
