void mainImage( out vec4 O, in vec2 U ) {
	vec3 p, d = normalize( vec3(U.xy - iResolution.xy/2., iResolution) );

    float i, m;
	for(; ++i < 90. ;)
    {
        m = 1. - length(p.xy) + texture(iChannel0, vec2(p.z + iTime, atan(d.x, d.y)) / 6.28).r;
		if(m < .01)break;
		p += d * m/3.;
    }

	O = vec4(1) - i / 90.;
}
