float sdfCircle(vec2 point,float radius){

    return length(point)-radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord-iResolution.xy) / iResolution.x;
    uv *= 100.;

    vec3 fontColor = vec3(0.,1.,0.);

    vec3 outlineColor = vec3(1.,0.,0.);

    vec3 backColor = vec3(1.);
    float smoothDelta = 2.;
    float outlineDelta = 4.;

    float dis = sdfCircle(uv,30.);

    float alpha = 1.-smoothstep(-smoothDelta,smoothDelta,dis);
    float outlineAlpha = 1.-smoothstep(-outlineDelta,outlineDelta,dis);

    fontColor = fontColor*alpha;

    outlineColor *= outlineAlpha;


    fontColor = mix(outlineColor,fontColor,alpha);

    fragColor.rgb = fontColor;

    fragColor.rgb = mix(backColor,fontColor,alpha);

}
