void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 pc = vec4( 1.0, 1.0, 1.0, 1.0 );

    vec4 texture1 = texture( iChannel0, uv );
    vec4 texture2 = texture( iChannel1, uv );
    vec4 texture3 = texture( iChannel2, uv );
    vec4 texture4 = texture( iChannel3, uv );

    pc = texture1;    
    if( (texture1.r <= 0.8) && (texture1.b <= 0.30) && (texture1.g > 0.33) )
    {
    	pc = texture2;
    }

    if( (texture2.r <= 0.8) && (texture2.b <= 0.30) && (texture2.g > 0.3) )
    {
    	pc = texture3;
    }
    if( (texture3.r <= 0.8) && (texture3.b <= 0.30) && (texture3.g > 0.33) )
    {
    	pc = texture4;
    }
    if( (texture4.r <= 0.8) && (texture4.b <= 0.30) && (texture4.g > 0.33) )
    {
    	pc = vec4(0.0);
    }


	fragColor = vec4( pc );
}
