float pulseCircle(vec2 p, float r)
{
    float l = length(p) - r;
    return smoothstep(2.5,3.0,2.*sin(3.5/l + 6.*iTime) + 0.2/l);
}

float hash(vec2 co){
    return sin( 2355.44 * fract(sin(dot(co.xy ,vec2(12.9898,78.233)))  * 43758.5453));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;    

    vec3 bg = vec3(0.1, 0.1, 0.1);
    vec3 color = vec3(hash(uv*iTime*0.2));

    bg = mix(bg, color, pulseCircle(uv - vec2(0.9, 0.5), 0.20));

    fragColor = vec4(bg, 1.0);

}
