#define SAMPLES 256
#define THRESHOLD .92
#define INTENSITY .5

vec3 sample(vec2 uv) {
    vec4 col = texture(iChannel0, uv);
    if((col.x+col.y+col.z)/3. < THRESHOLD) {
        col.xyz = vec3(0.);
    }
//    col = clamp(col-THRESHOLD,0.,1.);
    return col.xyz;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);

    vec2 center = vec2(.25+.25*cos(iTime*1.5),.75+.15*sin(iTime));
    if(iMouse.z > 0.) {
        center = iMouse.xy/iResolution.xy;
    }

    vec3 sum 	 = vec3(0.);
    float weight = 1. / float(SAMPLES);
    for(int i = 0; i < SAMPLES; i++) {
        sum += sample(uv);

        vec2 dir = center-uv;
        uv += dir * .01;
    }

    fragColor.xyz += sum * weight * INTENSITY;
    fragColor.w    = 1.;
}
