#define colorWidth 0.2	// Ranges from 0.0 to 1.0
#define colorCenter 0.7	// Ranges from 0.0 to 1.0
#define repetition 5.0	// Ranges from 1.0 to infinity (starts to look ridiculous past 20

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 texture = texture(iChannel0, uv, -1.0);
    float average = (texture.r + texture.g + texture.b) / 3.0;
    average *= repetition;
	float red = sin(iTime + average + 2.0) * colorWidth + colorCenter;
	float green = sin(iTime + average + 0.0) * colorWidth + colorCenter;
	float blue = sin(iTime + average + 4.0) * colorWidth + colorCenter;
	fragColor = vec4(red, green, blue, 1.0);
}
