//simple fade between two images

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //normalize the xy space 0.0 - 1.0 in one line
	vec2 st = fragCoord.xy / iResolution.xy;

    //get the pixel at the xy from the image
    vec4 image1 = texture(iChannel0, st);
	vec4 image2 = texture(iChannel1, st);

    float pct = abs(sin(iTime));

    // Mix uses pct (a value from 0-1) to
    // mix the two colors
    vec4 color = mix(image1, image2, pct);

    // Output to screen
    fragColor = vec4(color);
}
