void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy,
         M = iMouse.xy;
    U /= R;
    float m = M.x/R.x, 
          x = U.x,
          L = 10.,
        v = smoothstep(1.,m,abs(2.*x-1.));
    //v *= exp2(-L*x);
    O = vec4(U.y < v);

    v = .5 - .5*cos(6.28*x);
    //v *= exp2(-L*x);
    O.r = float(U.y < v);
}
