void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  float t = iTime;

  vec2 uv = fragCoord.xy / iResolution.xy;

  float vol = texture(iChannel0, vec2(abs(sin(t) * 100.0), 1.0)).x;

  float w = 0.0;

  w = sin(
    sin(iTime * 13.5 + (3.0 * vol)) *
    10.0 *
    pow(
      pow(uv.x, 1.0 + (2.0 * sin(iTime * 0.3))) + sin(uv.y *  + (19.0 * vol)) / 10.0,
      (4.7 * sin(iTime * 0.2)) + (1.5 * sin(iTime * 0.2))
    )
  );

  fragColor = vec4(w, w, w, 1.0);

}
