void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    int iter = int (iTime);
    vec2 c = vec2(.32, .32);

    vec2 xy = fragCoord.xy / iResolution.xy;
    vec2 z;
    z.x = 3.0 * (xy.x - 0.5);
    z.y = 2.0 * (xy.y - 0.5);

    int i;
    for(i=0; i<iter; i++) {
        float x = (z.x * z.x - z.y * z.y) + c.x;
        float y = (z.y * z.x + z.x * z.y) + c.y;

        if((x * x + y * y) > 4.0) break;
        z.x = x;
        z.y = y;
    }

    fragColor = vec4(1.0,1.0,1.0,0.0) * (i == iter ? 0.0 : float(i)) / 10.0 + vec4(0, 0, 0, 1.0);
}
