vec2 pfromi(in float i) {
   return vec2( mod(i,6.)+sin(i+iTime)*1. , i/6.+cos(i+iTime)*.1 )*.3; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy;

	int sm = 1;
    float ld = 999999.,db;
    vec2 p;
    for (int i = 1; i < 22; i++) {
        p = pfromi(float(i));
        db = abs(p.x-uv.x)+abs(p.y-uv.y);
        if (db < .01) {
            fragColor = vec4(0.);
            return;
        }
        if (db < ld) {
            sm = i;
            ld = db;
        }
    }


    if (abs(uv.x+.2-mod(iTime*.1,1.4))-.2 < 0.) {
        fragColor = vec4(ld*1.);
    } else {
    	fragColor = texture(iChannel0,vec2(float(sm)*.02));
    }
}
