void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;


    float speed = 1.0/2000.0;

    float x1 = uv.x+cos(iTime/11.613*speed)*2.0;
    float y1 = uv.y+sin(iTime/12.5*speed)*2.0;

    float x2 = uv.x+cos(iTime/13.414*speed)*2.0;
    float y2 = uv.y+sin(iTime/14.31*speed)*2.0;

    float x3 = uv.x+cos(iTime/15.215*speed)*2.0;
    float y3 = uv.y+sin(iTime/16.13*speed)*2.0;


    float sum1 = sqrt((x1*x1)+(y1*y1));

    sum1 *= sqrt((x1*x1)+(y1*y1));
    sum1 *= sqrt((x1*x1)+(y1*y1));
    sum1 =  sin(sum1 * (( sin(iTime/10.5)+1.1)*10.0)  );



    float sum2 = sqrt((x2*x2)+(y2*y2));

    sum2 *= sqrt((x2*x2)+(y2*y2));
    sum2 *= sqrt((x2*x2)+(y2*y2));
    sum2 =  sin(sum2 * (( sin(iTime/10.6)+1.1)*10.0)  );



