    sum3 *= sqrt((x3*x3)+(y3*y3));
    sum3 *= sqrt((x3*x3)+(y3*y3));
    sum3 =  sin(sum3 * (( sin(iTime/10.77)+1.1)*10.0)  );


    float sum = sum1+sum2+sum3;

	fragColor = vec4(sum,sum,sum,1.0);
