//#define VISUALIZE_COOL

#define SAMPLES 5


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - .5;
    uv *= 3.;//4.5+sin(iTime*.25)*2.;
    vec2 ruv = uv;

    fragColor = vec4(0.);

    for (int b = 0; b < SAMPLES; b++) {

        uv = ruv+(cos(uv*.7521+float(b)*.42)+cos(uv.yx*.2234+float(b)*2.42))*.3;

        for (int i = 0; i < 14; i++) {
            vec2 dir = -normalize(abs(mod(uv,.5)-.25));
            uv = uv.yx + dir * .2 * sin(atan(uv.y,uv.x)+ (cos(iTime*.14)+4.) );
            uv = abs(uv);
        }

       // #ifdef VISUALIZE_UV
       // fragColor += vec4(cos(normalize(abs(uv))*12.)*.5+.5,0.,1.0);
       // #endif

      //  #ifdef VISUALIZE_CIRCLE_DF
      //  float dist = (length(uv)-.5) + iTime*.03;
      /*  fragColor += vec4(sin(dist*3.5+.46),
                         cos(dist*4.6),
                         -sin(dist*8.6), 1.)*.5+.5;
     //   #endif
*/
       // #ifdef VISUALIZE_COOL
        fragColor += vec4(cos(uv*10.)*.5+.5
                        ,cos(length(uv)*4.)*.5+.5,1.);
       // #endif

    }

    fragColor /= float(SAMPLES);
}
