const float radius = .5;
const float Pi = 3.14;

float xyFunc(in vec2 coords) { // refactor via @FabriceNeyret2
	coords = sin(coords * coords);
	return step(1., coords.x + coords.y);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5*iResolution.xy) / iResolution.x; // via @FabriceNeyret2
    fragColor = vec4(xyFunc(uv*iTime + uv * 2. * sin( iTime + Pi ) ));
}
