// Ref: http://adrianboeing.blogspot.in/2011/02/ripple-effect-in-webgl.html
void mainImage( out vec4 fragColor, in vec2 fragCoord )  {
	// === MAIN ===
    // pixel position normalised to [-1, 1]
	vec2 cPos = 1.0 - 2.0 * fragCoord.xy / iResolution.xy;

	// === RIPPLES ===
	vec2 rPos1 = vec2(-0.5, -0.7);
	vec2 rPos2 = vec2(0.3, 0.7);
	vec2 rPos3 = vec2(0.5, -0.5);
	vec2 rPos4 = vec2(0.01, 0.01);
	float rStr1 = 12.0;
	float rStr2 = 8.0;
	float rStr3 = 5.0;
	float rStr4 = 60.0;

	// === FINAL ===
	float rLength1 = length(cPos + rPos1); // distance of current pixel from ripple center
	float rLength2 = length(cPos + rPos2); // distance of current pixel from ripple center
	float rLength3 = length(cPos + rPos3); // distance of current pixel from ripple center
	float rLength4 = length(cPos + rPos4); // distance of current pixel from ripple center
	vec2 uv = fragCoord.xy / iResolution.xy
		+ (rPos1 / rLength1) * cos(rLength1 * rStr1 -iTime * (rStr1 / 3.0)) * (rStr1 / 400.0)
		+ (rPos2 / rLength2) * cos(rLength2 * rStr2 -iTime * (rStr2 / 3.0)) * (rStr2 / 400.0)
		+ (rPos3 / rLength3) * cos(rLength3 * rStr3 -iTime * (rStr3 / 3.0)) * (rStr3 / 400.0)
		+ (rPos4 / rLength4) * cos(rLength4 * rStr4 -iTime * (rStr4 / 3.0)) * (rStr4 / 400.0);
    vec3 col = texture(iChannel0, uv).xyz;
	fragColor = vec4(col, 1.0);
}
