// graph : y=x*x

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;  // uv : range 0..1

    float x=uv.x; 
    float y=uv.y;

    float diff=x*x-y;// get diff of equation
    float intensity = 1./ diff;// the bigger diff, the lower intensity
    float band=0.005;// line width adjustment

    fragColor=vec4(abs(intensity*band), 0, 0, 1.);
}
