#define BORDER 0.6

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	// Compute the attenuation factors.
	vec2 fc = 1.0 - smoothstep(vec2(BORDER), vec2(1.0), abs(2.0*uv-1.0));
    float fact = fc.x * fc.y;
    fragColor=vec4(fact);
}
