void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float threshold = iMouse.x;
    float amount = iMouse.y/iResolution.y*2.;

    if(iMouse.y < 0.5)
        amount = 0.5+0.5*sin(iTime);

    vec4 original = texture(iChannel0, fragCoord/iResolution.xy);
    vec4 blured   = texture(iChannel1, fragCoord/iResolution.xy);
    fragColor = original + amount*(original-blured);
    //fragColor = blured;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.);

    float radius = 50.*iMouse.x/iResolution.x+1.;

    if(iMouse.x < 0.5)
        radius = 20.;

    float weight = 0.;

    vec2 uv = fragCoord/iResolution.xy;

    for(float i = -radius; i<= radius; i+= 1.0)
    {
        for(float j = -radius; j<= radius; j+= 1.0)
        {
            if(i*i+j*j<radius*radius)
            {
                float w = 1.0 - sqrt((i*i+j*j)/(radius*radius));
                //fragColor += w*texelFetch(iChannel0, ivec2(fragCoord)+ivec2(i,j), 0);
                fragColor += w*texture(iChannel0, uv+vec2(i,j)/iResolution.xy);
                weight += w;
                //weight += (i*i+j*j)/(radius*radius);
            }
        }
    }

    fragColor /= weight;
}
