#define PI 3.14159 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 pixelP = fragCoord.xy / iResolution.xy;

    vec3 color = vec3( 1.0, 1.0, 1.0 );

    float isNearZeroIfTrue = (pixelP.y * 3.0 - (PI / 2.0)) - sin( pixelP.x * iTime * iTime );

    color *= smoothstep( 0.0, 0.1, isNearZeroIfTrue );

    fragColor = vec4( color, 1.0 );
}
