#define PI 3.1415972


vec2 speed(vec2 p)
{
    return texture(iChannel1, p).xy;
}

vec3 noise(vec2 p)
{
    return texture(iChannel0, p*.5+vec2(iTime*.01)).rgb;
}


// from https://www.shadertoy.com/view/MsS3Wc
vec3 hsv2rgb_smooth( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	rgb = rgb*rgb*(3.0-2.0*rgb); // cubic smoothing	

	return c.z * mix( vec3(1.0), rgb, c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 p_p = uv;
    vec2 p_m = uv;

    float weight = 0.;
    vec3 val = noise(uv);
    weight = 1.;

    float dt = 0.01, t_max = 1.;
    for(float t=dt; t<=t_max; t+=dt)
    {
        float cur_weight = (t_max - t) / t_max;
        cur_weight = 1.;

        val += (noise(p_p) + noise(p_m))*cur_weight;
        weight += 2.0 * cur_weight;

        p_p += dt * speed(p_p);
        p_m -= dt * speed(p_m);
    }

    val /= weight;

    //fragColor = texelFetch(iChannel2, ivec2(65,2),0).x>0.5 ? vec4(hsv2rgb_smooth(vec3(atan(speed(uv).y,speed(uv).x)/2./PI, 0.5, 1.0-exp(-length(speed(uv))))),1.) : vec4(vec3(val),1.0);
    fragColor = vec4(val,1.0);

}

#define PERIOD	1.
#define EPSILON	0.05

#define SPEED_EV	.5
#define SPEED_AM	(0.5+iMouse.y/iResolution.y)

// from https://www.shadertoy.com/view/4djSRW
#define HASHSCALE3 vec3(.1031, .1030, .0973)

vec3 hash(vec3 p3)
{
	p3 = fract(p3 * HASHSCALE3);
    p3 += dot(p3, p3.yxz+19.19);
    return -1.0 + 2.0*fract((p3.xxy + p3.yxx)*p3.zyx);

}

// from https://www.shadertoy.com/view/Xsl3Dl
float noise( in vec3 p )
{
    vec3 i = floor( p );
    vec3 f = fract( p );

	vec3 u = f*f*(3.0-2.0*f);

    return mix( mix( mix( dot( hash( i + vec3(0.0,0.0,0.0) ), f - vec3(0.0,0.0,0.0) ), 
                          dot( hash( i + vec3(1.0,0.0,0.0) ), f - vec3(1.0,0.0,0.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,0.0) ), f - vec3(0.0,1.0,0.0) ), 
                          dot( hash( i + vec3(1.0,1.0,0.0) ), f - vec3(1.0,1.0,0.0) ), u.x), u.y),
                mix( mix( dot( hash( i + vec3(0.0,0.0,1.0) ), f - vec3(0.0,0.0,1.0) ), 
                          dot( hash( i + vec3(1.0,0.0,1.0) ), f - vec3(1.0,0.0,1.0) ), u.x),
                     mix( dot( hash( i + vec3(0.0,1.0,1.0) ), f - vec3(0.0,1.0,1.0) ), 
                          dot( hash( i + vec3(1.0,1.0,1.0) ), f - vec3(1.0,1.0,1.0) ), u.x), u.y), u.z );
}




vec2 speed(vec2 uv)
{
    float ratio = iResolution.y/iResolution.x;
    uv = uv*10.*vec2(1., ratio);
    return SPEED_AM*vec2(noise(vec3(uv, iTime*SPEED_EV)), noise(vec3(uv + vec2(12.10135, 1354.2435), iTime*SPEED_EV)));
}


void mainImage( out vec4 O, in vec2 C )
{
	vec2 r  = iResolution.xy;
	vec2 uv = C.xy / r.xy;


    O = vec4(speed(uv), 0., 0.);
}
