renderpass.code:
#define P(r,a)   (r) * vec2(cos(a),sin(a))                               // to polar
#define S(v,tk)  smoothstep( 2./R.y, -2./R.y, abs(v)-(tk) )              // draw bar (antialiased)
#define C(p,r) ( S( length(U-p), r ) + S( length(U-p*vec2(1,-1)), r ) )  // draw 2 sym disks
#define B        O += (1.-O)*                                            // blend

void mainImage( out vec4 O, vec2 U )
{
	vec2 R = iResolution.xy,
         V = U = ( U+U - R ) / R.y;
    O -= O;

    float p = .6283, x,y,                           //     = 2.Pi/10
          a = mod( atan(U.y,U.x) + p, p+p) - p;     // 2Pi/5 symmetry
    U = P( length(U), a ) * 1.25;                        
    x = U.x; y = U.y = abs(U.y);                    // mirror symmetry in each fan

	B S( x-.6  -  .4*y , .01 );                     // exterior thin wall
	B S( x-.67 + 1.2*y , .01 ) * S( abs(y), .04) * .6;    

	B S( x-.5 - .4*y , .05 )                        // thick wall
      * max( S( y, .45 ),
             C( P(.83, p),.07 )  );

    B S( x-.46, .06 ) * S( y, .19 )                 // interior bar attached to thick wall
      * ( 1. - C (vec2(.477, .18),.045 ) );

    U *= .72;
    B S( U.x-.5 - .4*U.y , .05 ) *.3                // exterior pit ( by scaling thick wall )
      * max(   S( U.y, .45 ) ,
               C( P(.83, p),.07 )  ) 
      * (.6+.4*cos(200.*a));                        //     radial strips

    B S( x-1.7 - .4*y , .9 ) *.3 
      * max( 0.,  cos(200.*V.y) - .6 );             // background strips ( V: before 5-sym )

  //O += (1.-O)*.3;                                 // B&W background
    O = mix( vec4(1,.95,.6,1),vec4(.6,.3,.3,1), O); // background + color scheme
}
