void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	const int NUMBER_OF_POINTS = 12;
	const vec2[NUMBER_OF_POINTS] points = vec2[](vec2(166.0f,43.0f),
                                                 vec2(583.0f,148.0f),
                             					vec2(80.0f,271.0f),
                                                vec2(787.0f, 426.0f),
                                                vec2(254.0f, 267.0f),
                                                vec2(258.0f, 407.0f),
                                                vec2(335.0f, 429.0f),
                                                vec2(747.0f, 339.0f),
                                                vec2(367.0f, 182.0f),
                                                vec2(603.0f, 79.0f),
                                                 vec2(551.0f, 446.0f),
                                                 vec2(260.0f, 230.0f));

    const vec4[NUMBER_OF_POINTS] cols = vec4[](vec4(0.22f, 0.55f, 0.06f, 1.0f),
                                               vec4(0.48f, 0.47f, 0.95f, 1.0f),
                                               vec4(0.47f, 0.02f, 0.58f, 1.0f),
                                               vec4(0.64f, 0.50f, 0.63f, 1.0f),
                                               vec4(0.89f, 0.16f, 0.60f, 1.0f),
                                               vec4(0.54f, 0.80f, 0.92f, 1.0f),
                                               vec4(0.22f, 0.17f, 0.16f, 1.0f),
                                               vec4(0.95f, 0.78f, 0.48f, 1.0f),
                                               vec4(0.54f, 0.71f, 0.90f, 1.0f),
                                               vec4(0.85f, 0.53f, 0.59f, 1.0f),
                                               vec4(0.15f, 0.30f, 0.99f, 1.0f),
                                              	vec4(0.45f, 0.86f, 0.31f, 1.0f));

    bool isPoint = false;
    int minIndex = 0;
    float minLength = 100000000.0f;

    for (int i = 0; i < NUMBER_OF_POINTS; i++){
        float l = length(fragCoord - points[i]); 
        if (l < 10.0){
            isPoint = true;
        }else if (l < minLength) {
                minIndex = i;
                minLength = l;
        }
   	}

    if (isPoint){
        fragColor = vec4(0,0,0,1);
    }else{
       fragColor = cols[minIndex];
    }


}
