void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Define the background color to a dark blue using RGB
    vec3 backgroundColor = vec3(0.25, 0.33, 0.70);


    // Define the resolution of the circle
	vec2 coordinates = (fragCoord.xy-iResolution.xy*0.9) / iResolution.y;

    // Define the circle color to a light blue circle using RGB
    vec3 color = vec3(0.20, 0.83, 0.88);
    // Define an extra color 
    vec3 extraColor = vec3(0.20, 0.1, 0.88);

    // Define The time the circle takes to finish a cycle
    float circleTime = fract(iTime*0.99);

    float cirlceLength = length(coordinates);

    float thickness = 0.01;

    float res = 1.0/iResolution.y;

    color = color * ((smoothstep(circleTime+thickness+res, circleTime+thickness, cirlceLength))); 

    // Set the fragColor using the defined colors above
    fragColor = vec4(color + backgroundColor + extraColor,  1);



}
