const float divisions=10.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 corrected_coord = vec2(fragCoord.x, iResolution.y - fragCoord.y);
    float biggest_dim = max(iResolution.x, iResolution.y);
    vec2 st = corrected_coord/biggest_dim;

    vec3 color = vec3(1.);
    // PARAMETERS

    //float time = 0.5;

    // CODE
    float t = fract(iTime)*3.-1.;
    vec2 f_st = fract(st*divisions);
    vec2 i_st = floor(st*divisions);
    f_st -= 0.5;
    t = (1.-t+(i_st.x/divisions) - (1.-i_st.y/divisions));
    float a = (step(t, 1.-abs(f_st.x+f_st.y))*step(t, 1.-abs((f_st.x)-(f_st.y))));
    //result_color.a = 1-(step(t, abs(f_st.x+f_st.y))+step(t, abs((1-f_st.x)-(1-f_st.y))));
    fragColor = texture(iChannel0, st) * (1.-a);
}
