void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = texture(iChannel0, uv);
}

mat2 rot(float a) {
    return mat2(cos(a), -sin(a), sin(a), cos(a));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 nc = 0.6*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    vec2 uv = 1.0 * fragCoord/iResolution.xy;

    nc*=rot(iTime*0.025);

    nc.y+=iTime*0.024;

    vec3 t = texture(iChannel0, nc).xyz;

    float l = length(t);
    t += l;
    vec3 col = vec3(0);
    vec3 depCol = vec3(1.0, 1.0, 0.0);

    if(l > 0.33)
    {
    	uv.y += 0.007;

    	vec3 b = texture(iChannel1, uv).xyz;
    	b *= 0.9;

        col = b;

        col *= 0.85;

        col += mix(col, vec3(0), 0.5)*0.14;
    }
    else if(l <0.2)
    {  
    	uv.y -= 0.007;

    	vec3 b = texture(iChannel1, uv).xyz;

        col = b;

        col *= 0.80;

        col += mix(col, vec3(0.0, 0.0, 0.0), 0.5)*0.14;

    }
    else
        col = t * 1.4;

    fragColor = vec4(col,1.0);
}
