void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy-.5;
    uv.x *= iResolution.x/iResolution.y;
    float RayDir = sin(iTime)/3.-.8;
    float SquareSDF =
                          min(max(abs(uv.x),abs(uv.y))-.1,.0)
                          +
                          length(max(abs(uv)-.1,0.))
                          ;


    vec3 col = vec3(sin(SquareSDF*100.+(iTime*10.)));
    vec2 ray = vec2(.5,-.5);
    for (int i = 0; i < 7; i++){
        SquareSDF =
                          min(max(abs(ray.x),abs(ray.y))-.1,.0)
                          +
                          length(max(abs(ray)-.1,0.))
                          ;
        if (length(uv-ray) < SquareSDF && length(uv-ray) > SquareSDF - .005) col = vec3(1.,0.,0.);
        ray += vec2(sin(RayDir),cos(RayDir))*SquareSDF;
    }
    fragColor = vec4(col,1.0);
}
