//iq noise
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);
	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
	vec2 rg = textureLod( iChannel0, (uv+ 0.5)/256.0, 0.0 ).yx;
	return 1. - 0.82*mix( rg.x, rg.y, f.z );
}

void mainImage( out vec4 c, in vec2 v )
{
	vec2 p =  v.xy/iResolution.xy;
    vec4 b = texture(iChannel0,p);
    for (float i = 0.; i < 1.; i+=.2)
    {
        vec4 d = texture(iChannel0, p * i + noise(vec3(i + iTime))/10.);
        b += pow(d, vec4(2.5));
    }
	c = b*.2;
}
