void mainImage( out vec4 fragColor, in vec2 C )
{
    vec2  c = C / iResolution.xy;
    float y = sin(c.x * 16.0 + iTime);
    y *= y * y * y;
    float d = pow(max(0.0, 1.0 - abs(c.y + y * 0.5 - 0.75)), 64.0);
    d *= abs(sin(C.x)) + 0.75;
    d *= abs(sin(C.y)) + 0.75;
    d *= abs(sin(iTime * 1024.0)) + 0.75;
	fragColor = vec4(vec3(d * 0.5, d + 0.05, d * 0.5), 1.0);
}

float getSample( float time ) {
    float k1 = 1.0;
    float k2 = 5.0;
    float k3 = 2048.0;
    float t = time * 2.0;
    float a = k1 * sin(k2 * sqrt(k3 * fract(t)));    
    if (fract(time * 0.25) > 0.5 && fract(time) > 0.75)
        a = sin(time * 1024.0) * 0.5;
	return a;    
}

vec2 mainSound( in int samp, float t )
{    
    float a = 0.0;
    for (float i = 1.0; i < 5.0; i++)
    	a += getSample(t - i * 0.13) / i;
    return vec2(a);
}
