uniform vec3 col [6];
#define si sin(iDate.w)
#define sinus si*si-.3
void mainImage( out vec4 c, vec2 U )
{

    //def colors
vec3 col [6];col[1]=vec3(1,0,0);col[3]=vec3(1,1,0)*.9;
	col[5]=vec3(0,0,1);    col[2]=col[1]+col[3]/2.;
        col[4]=vec3(0,1,0)*.8;    col[0]=col[1]+col[5]/2.;
    //////

    vec2 R=iResolution.xy;
    float d=acos(-1.)/180.;
    vec2 fixx=vec2( cos(d*60.) , -sin(d*60.));
    vec2 fixy=vec2( sin(d*60.) , cos(d*60.));
    mat2 mux=mat2(fixx,fixy);
	U/=R.y;U-=.5;U.x-=.4;
	c = vec4(.5);

     float r=.2+abs(sinus/2.);

    if ( (length(U)<r) && (abs(U.x)<r*.866)  )   
    {c+=.5;  

    //draw hex
    if ((abs(U.x)<r*.866)&&(abs(U.y)+abs(U.x)*.5/.866<r)) c-=.3;
    r*=.9;
    if ((abs(U.x)<r*.866)&&(abs(U.y)+abs(U.x)*.5/.866<r)) c-=.3;
     r/=.95;
    }


    // dots
    vec2 c12=vec2(0,1)*r;  
    c12*=mux*mux;
    for (int x=0;x<6;x++)
    {if (length((c12*=mux)-U)<length(U)/9.) c+=vec4(col[x],1)-.3;//c+=.1;
    }
}


