void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

//#define USE_RGB
#define ROTATE

#define TWO_PI 6.283185

vec2 angledUv(vec2 uv, float intensity){
    float angle = intensity * TWO_PI;
#ifdef ROTATE 
    angle += iTime * 3.;
#endif
	uv.x += cos(angle)/iResolution.x*3.;
    uv.y += sin(angle)/iResolution.y*3.;
	return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord / iResolution.xy;
    vec4 tex = texture(iChannel0, uv);

    float grey = dot(tex.rgb, vec3(0.2126, 0.7152, 0.0722));

    vec2 aUv = angledUv(uv, grey);
    fragColor = texture(iChannel1, aUv);

    //fade
#ifdef USE_RGB 
    fragColor = mix(fragColor, tex, 0.03);
#else    
    fragColor = mix(vec4(fragColor.r), vec4(grey), 0.03);
#endif
}
